    def Save(self):
        Pass1=self.pass1_txt.text()
        Pass2=self.pass2_txt.text()
        FarmerCode=self.F_code_txt.text()
        BankName=self.B_name_txt.text()
        FarmerName=self.F_name_txt.text()
        AccNo=self.Account_no_txt.text()
        City=self.city_txt.text()
        Branch=self.Branch_txt.text()
        IFSCcode=self.IFSC_txt.text()
        conn = sqlite3.connect('D:\Internship\ERP_Info\ERP_Info.db')
        cur = conn.cursor()
        cur.execute('INSERT INTO BankInfoDB (Pass1,Pass2,FarmerCode,BankName,FarmerName,AccNo,Branch,City,IFSCcode) \
                                                    VALUES (?,?,?,?,?,?,?,?,?)', (Pass1,Pass2,FarmerCode,BankName,FarmerName,AccNo,Branch,City,IFSCcode))

        conn.commit()
        print("Records Inserted successfully")
        conn.close()
        self.View_data()

    def View_data(self):
        conn = sqlite3.connect('D:\Internship\ERP_Info\ERP_Info.db')
        cur = conn.cursor()

        cur.execute('select FarmerName from BankInfoDB')
        hu = cur.fetchall()
        self.B_namez_lbl.clear()
        for i in range(len(hu)):
            item = QtWidgets.QListWidgetItem(hu[i][0])
            self.B_namez_lbl.addItem(item)

        conn.commit()
        print("Records Displayed successfully")
        conn.close()

