    def selectionChanged(self):
        self.LE_Name.setEnabled(False)
        name = self.listWidget_name.currentItem()
        cname = (name.text())
        self.LE_Name.setText(name.text())
        conn = sqlite3.connect('D:\Internship\ERP_Info\ERP_Info.db')
        cur = conn.cursor()
        cur.execute("SELECT Code FROM CenterDB WHERE Name=?", (cname,))
        result = cur.fetchone()
        # print(result)
        # print(type(result))
        res = int(''.join(map(str, result)))
        self.LE_Code.setText(str(res))
        conn.commit()
        # print("Code Displayed successfully")
        conn.close()

    def filterlist(self):
        self.listWidget_name.clear()

    def add_data(self):
        self.LE_Name.setEnabled(True)
        self.LE_Name.setFocus()
        lastcode = self.listWidget_name.count()
        code = lastcode + 1
        print(code)
        self.LE_Code.setText(str(code))

    def save_data(self):
        LE_Code = self.LE_Code.text()
        LE_Name = self.LE_Name.text()
        if LE_Code == '' and LE_Name == '':
            # Create the dialog without running it yet
            msgBox = QMessageBox()

            # Set the various texts
            msgBox.setWindowTitle("Information")
            msgBox.setText(
                "No entry '" + str(LE_Code) + " " + str(LE_Name) + "'.Please enter Proper Name")
            msgBox.setStandardButtons(QMessageBox.Yes | QMessageBox.No)
            msgBox.setDefaultButton(QMessageBox.No)

            # Run the dialog, and check results
            bttn = msgBox.exec_()
            if bttn == QMessageBox.Yes:
                return True
            else:
                return False
        else:
            code = self.listWidget_name.count()
            if code == 0:
                code = 1
            else:
                code = code + 1
            self.LE_Code.setText(str(code))
            conn = sqlite3.connect('D:\Internship\ERP_Info\ERP_Info.db')
            cur = conn.cursor()
            cur.execute('INSERT INTO CenterDB (Code,Name) \
                                                           VALUES (?,?)', (code, LE_Name))

            conn.commit()
            print("Records Inserted successfully")
            conn.close()
            self.View_data()
            self.LE_Name.clear()
            self.LE_Name.setFocus()

    def View_data(self):
        conn = sqlite3.connect('D:\Internship\ERP_Info\ERP_Info.db')
        cur = conn.cursor()
        cur.execute('select Name from CenterDB')
        hu = cur.fetchall()
        self.listWidget_name.clear()
        for i in range(len(hu)):
            item = QtWidgets.QListWidgetItem(hu[i][0])
            self.listWidget_name.addItem(item)

        conn.commit()
        print("Records Displayed successfully")
        conn.close()

    def Update_data(self):
        self.LE_Name.setEnabled(True)
        LE_Code = self.LE_Code.text()
        LE_Name = self.LE_Name.text()
        conn = sqlite3.connect('D:\Internship\ERP_Info\ERP_Info.db')
        cur = conn.cursor()
        cur.execute("UPDATE CenterDB SET Name = ? WHERE Code = ?", (LE_Name, LE_Code))
        conn.commit()
        print("Records Modify successfully")
        conn.close()
        self.View_data()

    def Delete_data(self):
        LE_Code = self.LE_Code.text()
        conn = sqlite3.connect('D:\Internship\ERP_Info\ERP_Info.db')
        cur = conn.cursor()
        cur.execute("DELETE FROM CenterDB WHERE Code=?", (LE_Code))
        conn.commit()
        print("Records Deleted successfully")
        conn.close()
        self.LE_Code.clear()
        self.LE_Name.clear()
        self.View_data()

